sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},

	isDatePast: function(strDate){
		var now = new GlideDateTime();
		var then = new GlideDateTime(strDate);

		var nowDate = now.getLocalDate();
		var thenDate = then.getLocalDate();

		if(nowDate.compareTo(thenDate) > 0){
			return true;
		}
		else{
			return false;
		}
	},

	isDateToday: function(strDate){
		var now = new GlideDateTime();
		var then = new GlideDateTime(strDate);

		var nowDate = now.getLocalDate();
		var thenDate = then.getLocalDate();

		if(nowDate.compareTo(thenDate) == 0){
			return true;
		}
		else{
			return false;
		}
	},

	type: 'NeedItUtils'
};
